package com.study.loan.util;

import org.apache.commons.codec.binary.Base64;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.io.*;

public class PictureUtil {
	private static Logger log = LoggerFactory.getLogger(ZipUtil.class);

	/**
	  * 对字节数组字符串进行Base64解码并生成图片
	  * @param data 图片数据
	  * @param path 保存图片全路径地址
	  * @param name 图片名称
	  */
	public static void picture(String data, String path, String name) {
		FileOutputStream fos = null;
		if(data.indexOf("base64,") != -1){
			data = data.substring(data.indexOf("base64,") + 7);
		}
		byte[] datas = Base64.decodeBase64(data);

		try {
			File file = new File(path);
			if (!file.exists()) { // 如果文件目录不存在
				file.mkdirs();
			}
			
			//写文件
			File picfile = new File(path + File.separator + name);
			fos = new FileOutputStream(picfile);
			fos.write(datas);
		} catch (IOException e) {
			log.error("照片打包出错", e);
		} finally {
			if (fos != null) {
				try {
					fos.close();
				} catch (IOException e) {
					log.warn("关闭IO异常");
				}
			}
		}
	}
	
	 /**
      * 将图片转换成Base64编码
      * @param imgFile 待处理图片
      * @return Base64编码格式的图片字符串
      */
     public static String getImgStr(String imgFile){
    	 File picfile = new File(imgFile);
		 if(!picfile.exists()){ // 如果文件不存在
			 // 返回文件未找到提示照片
			 return "data:image/jpeg;base64,";
		 }
			
         //将图片文件转化为字节数组字符串，并对其进行Base64编码处理
         InputStream in = null;
         byte[] data = null;
         //读取图片字节数组
         try
         {
             in = new FileInputStream(imgFile);
             data = new byte[in.available()];
             in.read(data);
             in.close();
         } 
         catch (IOException e)
         {
             e.printStackTrace();
         }
         return new String(Base64.encodeBase64(data));
     }
}
